from typing import List
class Solution:
    def chalkReplacer(self, chalk: List[int], k: int) -> int:
        allForOne = sum(chalk)
        remain = k % allForOne
        for index, value in enumerate(chalk):
            if remain >= value:
                remain -= value
            else:
                return index

    
sd = Solution()
